package Coollections_;

import java.util.ArrayList;

public class ArrayforEach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {1,345,324,5};
		for(int b:a)
		{
			System.out.println(b);
		}
		char ch[]= {'s','a','d','r'};
		for(char c:ch)
		{
			System.out.println(c);
		}
		
		
		
	}

}
